typedef struct {
    int fd;                // 0x20: file descriptor for ioctl operations
    uint32_t status;       // 0x24: sensor status or count
    char sensor_data[80];  // 0x28: sensor data buffer (0x50 bytes)
    void *sensor_mem;      // 0xac: pointer to allocated memory for sensor
    uint32_t init_status;  // 0xb0: sensor initialization status
    void *additional_mem;  // 0xb4: pointer to additional allocated memory for sensor
} ISPDevice;
